    <script>
        // Select all buttons with the class 'toggleButton'
        const buttons = document.querySelectorAll(".toggleButton");
        const form = document.getElementById("toggleForm");

        buttons.forEach(button => {
            button.addEventListener("click", function () {
                // Toggle form visibility
                form.classList.toggle("hidden");
            });
        });

        $(document).on('click', '#contactdata', function () {
            var csrfToken = $('meta[name="csrf-token"]').attr('content'); // Fetch CSRF token

            var formData = {
                _token: csrfToken, // Add CSRF token
                name: $("#name").val(),
                email: $("#email_add").val(),
                phone: $("#phone_add").val(),
                address: $("#address_add").val(),
                message: $("#message_add").val(),
                date: $("#datepicker").val(),
                time: $(".toggleButton.active").text().trim()
            };

            console.log("Selected Time:", formData.time);

            $.ajax({
                url: "https://pharma247.in/ready/to/get/store",
                type: "POST",
                data: formData,
                headers: {
                    'X-CSRF-TOKEN': csrfToken // Set CSRF token in request header
                },
                success: function (response) {
                    location.reload();
                }
            });
        });

        document.addEventListener("DOMContentLoaded", function () {
            const timeButtons = document.querySelectorAll(".toggleButton");

            timeButtons.forEach(button => {
                button.addEventListener("click", function () {
                    // Remove active class from all buttons
                    timeButtons.forEach(btn => btn.classList.remove("active"));

                    // Add active class to the clicked button
                    this.classList.add("active");
                });
            });
        });
    </script>
    <script>
        $(document).ready(function () {
            const modalScrollContainer = $('.modal-body.bookinggg');

            // Scroll to time picker inside the modal
            $('#datepicker').on('click', function () {
                const target = $('#time-picker');
                if (target.length) {
                    modalScrollContainer.animate({
                        scrollTop: modalScrollContainer.scrollTop() + target.position().top - 20
                    }, 500);
                }
            });

            // Scroll to form inside the modal
            $('#timepicker .toggleButton').on('click', function () {
                const target = $('#toggleForm');
                if (target.length) {
                    target.removeClass('hidden'); // unhide form if it's hidden
                    modalScrollContainer.animate({
                        scrollTop: modalScrollContainer.scrollTop() + target.position().top - 20
                    }, 500);
                }
            });
        });
    </script>
